  const getExitReasonColor = (reason) => {
    if (reason.includes('PROFIT') || reason.includes('TRAILING')) return 'text-emerald-600';
    if (reason.includes('STOP_LOSS')) return 'text-red-600';
    return 'text-gray-600';
